          {{{markdown (truncate (getProperty @root.data.profileInfo.notes Id) limit)}}}
          </div>
          <a href="/event/{{Id}}/{{title_slug}}{{> query_search}}#notes">Read more</a>
        {{/compare ~}}
      {{else}}
        {{{markdown (getProperty @root.data.profileInfo.notes Id)}}}
      {{/if ~}}
    </div>
  </div>
{{/compare ~}}

partials.favourite_chip:
{{#if @root.data.auth ~}}
  {{#> ajax_chip
    onUrl=(concat "/add-to-favourites?id=" id)
    offUrl=(concat "/remove-from-favourites?id=" id)
