partials.goodhq_hearts:
{{#if stars ~}}
  <ul class="hearts">
    {{#compare stars '<' 0.25 ~}}
      <li class="empty"><span aria-hidden="true" class="icon-heart"></span></li>
    {{/compare ~}}
    {{#compare stars '>=' 0.25 ~}}
      {{#compare stars '<' 0.75 ~}}
        <li class="half"><span aria-hidden="true" class="icon-heart"></span></li>
      {{/compare ~}}
      {{#compare stars '>=' 0.75 ~}}
        <li class="filled"><span aria-hidden="true" class="icon-heart"></span></li>
      {{/compare ~}}
    {{/compare ~}}
    {{#compare stars '<' 1.25 ~}}
      <li class="empty"><span aria-hidden="true" class="icon-heart"></span></li>
    {{/compare ~}}
    {{#compare stars '>=' 1.25 ~}}
      {{#compare stars '<' 1.75 ~}}
        <li class="half"><span aria-hidden="true" class="icon-heart"></span></li>
      {{/compare ~}}
      {{#compare stars '>=' 1.75 ~}}
        <li class="filled"><span aria-hidden="true" class="icon-heart"></span></li>
      {{/compare ~}}
    {{/compare ~}}
    {{#compare stars '<' 2.25 ~}}
      <li class="empty"><span aria-hidden="true" class="icon-heart"></span></li>
    {{/compare ~}}
    {{#compare stars '>=' 2.25 ~}}
      {{#compare stars '<' 2.75 ~}}
        <li class="half"><span aria-hidden="true" class="icon-heart"></span></li>
      {{/compare ~}}
      {{#compare stars '>=' 2.75 ~}}
        <li class="filled"><span aria-hidden="true" class="icon-heart"></span></li>
      {{/compare ~}}
    {{/compare ~}}
    {{#compare stars '<' 3.25 ~}}
      <li class="empty"><span aria-hidden="true" class="icon-heart"></span></li>
    {{/compare ~}}
    {{#compare stars '>=' 3.25 ~}}
      {{#compare stars '<' 3.75 ~}}
        <li class="half"><span aria-hidden="true" class="icon-heart"></span></li>
      {{/compare ~}}
      {{#compare stars '>=' 3.75 ~}}
        <li class="filled"><span aria-hidden="true" class="icon-heart"></span></li>
      {{/compare ~}}
    {{/compare ~}}
    {{#compare stars '<' 4.25 ~}}
      <li class="empty"><span aria-hidden="true" class="icon-heart"></span></li>
    {{/compare ~}}
    {{#compare stars '>=' 4.25 ~}}
      {{#compare stars '<' 4.75 ~}}
        <li class="half"><span aria-hidden="true" class="icon-heart"></span></li>
      {{/compare ~}}
      {{#compare stars '>=' 4.75 ~}}
        <li class="filled"><span aria-hidden="true" class="icon-heart"></span></li>
      {{/compare ~}}
    {{/compare ~}}
  </ul>
{{/if ~}}

partials.handle_404:
{{#if @root.data.primaryResponse.hits.hits.[0] ~}}
{{> @partial-block }}
{{else}}
<div class="mdc-layout-grid">
  <div class="mdc-layout-grid__inner">
    <div class="mdc-layout-grid__cell mdc-layout-grid__cell--span-8-desktop mdc-layout-grid__cell--span-4-tablet mdc-layout-grid__cell--span-4-phone content page-content">
      <h2>The page you requested could not be found</h2>
      <p>
        We recently relaunched our website and some page addresses may have changed.
      </p>
      {{#ifAll @root.data.opportunitySupplimentary.not_found (compare @root.data.opportunitySupplimentary.not_found.hits.total ">" 0) ~}}
      <h3>Did you mean?</h3>
      {{#each @root.data.opportunitySupplimentary.not_found.hits.hits ~}}
      {{#compare @index "===" 0 ~}}
      {{>highlight_result}}
      {{else}}
      {{/compare ~}}
      {{/each ~}}
      {{/ifAll ~}}
    </div>
    <div class="mdc-layout-grid__cell mdc-layout-grid__cell--span-4-desktop mdc-layout-grid__cell--span-4-tablet mdc-layout-grid__cell--span-4-phone">
      <img src="{{~>asset_url url='images/404.png'~}}" class="responsive">
    </div>
  </div>
</div>
{{#ifAll @root.data.opportunitySupplimentary.not_found (compare @root.data.opportunitySupplimentary.not_found.hits.total ">" 1) ~}}
<hr />
<h3>Other relevant content</h3>
{{#each @root.data.opportunitySupplimentary.not_found.hits.hits ~}}
{{#compare @index "===" 0 ~}}
{{else}}
{{>highlight_result}}
{{/compare ~}}
{{/each ~}}
{{else}}
<h3>Try searching for a topic</h3>
{{/ifAll ~}}

{{/if ~}}

