      {{/compare ~}}

      <div class="map-results" data-component="LeafletMap" data-LeafletMap='{{#json}}
        {{#json key="points" type="array"}}{{/json}},
        {{#json key="mapOptions"}}
          {{{json key="fullscreenControl" value=true}}},
