SELECT DISTINCT council, COUNT(email) AS submissions FROM newsletter_coronavirus WHERE consent_volunteering_updates = true AND consent_tsi = true AND location_postcode != '' AND council != '' AND cast(date_last_modified as date) between '{{#unless @root.request.params.query.from_date}}2020-03-29{{else}}{{@root.request.params.query.from_date}}{{/unless}}' and CURDATE() GROUP BY council ORDER BY council ASC;